    private void CalculateIBScore_ButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_CalculateIBScore_ButtonActionPerformed
        if(Group1Name_ComboBox.getSelectedItem() == Group6Name_ComboBox.getSelectedItem()||
                Group2Name_ComboBox.getSelectedItem() == Group6Name_ComboBox.getSelectedItem()||
                Group3Name_ComboBox.getSelectedItem() == Group6Name_ComboBox.getSelectedItem()||
                Group4Name_ComboBox.getSelectedItem() == Group6Name_ComboBox.getSelectedItem()||
                Group5Name_ComboBox.getSelectedItem() == Group6Name_ComboBox.getSelectedItem())
        {
          JOptionPane.showMessageDialog(jPanel2, "Please input distinct subjects!");
        }
        else
        {
            int TotalIBScore = Group1Score_ComboBox.getSelectedIndex() +Group3Score_ComboBox.getSelectedIndex() + 
     Group2Score_ComboBox.getSelectedIndex() + Group4Score_ComboBox.getSelectedIndex() + 
      Group6Score_ComboBox.getSelectedIndex() + Group5Score_ComboBox.getSelectedIndex() + 
       ExtrasName_ComboBox.getSelectedIndex() + ExtrasScore_ComboBox.getSelectedIndex() + 7;
        TotalIBScore_TextField.setText("" + TotalIBScore );
        }
    }//GEN-LAST:event_CalculateIBScore_ButtonActionPerformed

